    <script>
        (function () {
            var links = document.getElementsByClassName('schedule-wrapper__link');
            var onDaySwitch = function () {
                var clickScheduleDay = this.getAttribute('data-day');

                // Switch to clicked schedule day
                var scheduleDays = document.getElementsByClassName('schedule');
                for (var i = 0; i < scheduleDays.length; i++) {
                    scheduleDays[i].className = scheduleDays[i].id == clickScheduleDay ? 'schedule schedule--shown' : 'schedule';
                }

                // Change nav
                for (var i = 0; i < links.length; i++) {
                    if (links[i].className) {
                        links[i].className = 'schedule-wrapper__link schedule-wrapper__day';
                    }
                }
                this.className = 'schedule-wrapper__link schedule-wrapper__day-active';
            };
            for (var i = 0; i < links.length; i++) {
                links[i].addEventListener('click', onDaySwitch, false);
            }
        }.call(this));
